// === Lee tabla de verdad desde Aggregate1 ===
const agg = $items('Aggregate1', 0, 0)[0].json;

// Convierte columnas a arrays (maneja objetos "0","1",... o arrays)
const asArr = col => Array.isArray(col) ? col : Object.values(col || {});
const categorias = asArr(agg['Categoria']);
const tipos = asArr(agg['Gasto/Ingreso']);

// Normalizador (sin acentos, trim, min煤sculas)
const norm = s => String(s || '')
  .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
  .trim().toLowerCase();

// Diccionario categoria -> "Gasto"/"Ingreso"
const lookup = {};
categorias.forEach((c, i) => { lookup[norm(c)] = String(tipos[i] || '').trim(); });

// === Fecha/periodo desde variables de entorno ===
// Preferimos $today (00:00 UTC del d铆a actual); si no, $now.
const base = new Date($today || $now);
const months = ['Enero','Febrero','Marzo','Abril','Mayo','Junio','Julio','Agosto','Septiembre','Octubre','Noviembre','Diciembre'];

// Usamos getters UTC para evitar corrimientos por zona horaria
const dd = String(base.getUTCDate()).padStart(2, '0');
const mm = String(base.getUTCMonth() + 1).padStart(2, '0');
const yyyy = base.getUTCFullYear();

const fechaStr = `${dd}/${mm}/${yyyy}`;
const periodoStr = `${months[base.getUTCMonth()]} ${yyyy}`;

// === Procesa cada item del Split Out ===
return items.map(it => {
  // Tus datos est谩n dentro de "productos[0]"
  const row = it.json['productos[0]'] ?? it.json;

  const cat = norm(row.categoria);
  const tipo = lookup[cat] ?? 'DESCONOCIDO';

  // Guardamos SOLO dentro de productos[0]
  row.tipo = tipo;
  row.fecha = fechaStr;
  row.periodo = periodoStr;

  return it;
});
